mapper.select:
SELECT 
t.uuid,t.questionTitle,t.questionItems,t.questionAnswer,t.questionSkills,t.answerAnalysis,t.questionImg
FROM xckmy t where  t.tmlx=#{str1,jdbcType=VARCHAR}  
order by t.uuid limit #{int1,jdbcType=INTEGER},#{int2,jdbcType=INTEGER}

select t.uuid from tk_sc t where t.tkuuid=#{int1,jdbcType=INTEGER} 
and t.sclx=#{str2,jdbcType=VARCHAR}
and t.openid=#{str1,jdbcType=VARCHAR}

select t.uuid from tk_cw t where t.tkuuid=#{int1,jdbcType=INTEGER} and 
t.cwlx=#{str2,jdbcType=VARCHAR}
and t.openid=#{str1,jdbcType=VARCHAR}

mapper.delete:
	delete from tk_sc t where t.tkuuid=#{int1,jdbcType=INTEGER} 
and t.sclx=#{str2,jdbcType=VARCHAR}
and t.openid=#{str1,jdbcType=VARCHAR}
